export const i18nCode = `
// I18N Client-side library
(function(window) {
  const I18N = {
    currentLocale: localStorage.getItem('locale') || navigator.language || 'it-IT',
    translations: ${JSON.stringify(translations)},
    
    init() {
      this.updatePageLanguage()
      this.translatePage()
    },
    
    t(key) {
      const keys = key.split('.')
      let value = this.translations[this.currentLocale]
      
      for (const k of keys) {
        if (value && typeof value === 'object') {
          value = value[k]
        } else {
          return key
        }
      }
      
      return value || key
    },
    
    changeLanguage(locale) {
      this.currentLocale = locale
      localStorage.setItem('locale', locale)
      this.updatePageLanguage()
      this.translatePage()
    },
    
    updatePageLanguage() {
      const html = document.getElementById('htmlTag')
      if (html) {
        html.setAttribute('lang', this.currentLocale)
      }
    },
    
    translatePage() {
      document.querySelectorAll('[data-i18n]').forEach(el => {
        const key = el.getAttribute('data-i18n')
        el.textContent = this.t(key)
      })
      
      document.querySelectorAll('[data-i18n-placeholder]').forEach(el => {
        const key = el.getAttribute('data-i18n-placeholder')
        el.placeholder = this.t(key)
      })
    },
    
    formatCurrency(value) {
      const locale = this.currentLocale
      const currency = this.t('common.currency')
      return new Intl.NumberFormat(locale, {
        style: 'currency',
        currency: currency === 'R$' ? 'BRL' : currency === '€' ? 'EUR' : 'USD'
      }).format(value)
    }
  }
  
  window.I18N = I18N
})(window);
`
